INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (105, N'The Girl Who Played with Fire', 72, N'Quercus Grp', N'2010-07-29 00:00:00', N'/images/books/6d85cce4-6c23-4696-bb9d-dfced972b5cf.jpg', N'Main Hall', 1, N'Millennium publisher Mikael Blomkvist has made his reputation exposing corrupt establishment figures. So when a young journalist approaches him with an investigation into sex trafficking, Blomkvist cannot resist waging war on the powerful men behind this lucrative industry. When the journalist and his girlfriend are found murdered in their apartment, Lisbeth Salander''s fingerprints are found on the weapon left at the scene. Now hunted by the entire Swedish police force and officially branded a danger to society, she is forced to go undercover. But how can she prove her innocence? With her computer-hacking skills she is anything but the helpless victim.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/6d85cce4-6c23-4696-bb9d-dfced972b5cf.jpg', NULL)
