INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (113, N'A Short History of Tractors in Ukrainian', 77, N'Penguin Grp', N'2006-03-02 00:00:00', N'/images/books/f29d8b19-8526-4efe-977c-95697b550b5c.jpg', N'Main Hall', 1, N'When their recently widowed father announces he plans to remarry, sisters Vera and Nadezhda realise they must put aside a lifetime of feuding in order to save him. His new love is a voluptuous gold-digger from the Ukraine half his age, with a proclivity for green satin underwear and boil-in-the-bag cuisine, who stops at nothing in her single minded pursuit of the luxury Western lifestyle she dreams of. But the old man, too, is pursuing his eccentric dreams - and writing a history of tractors in Ukrainian. A wise, tender and deeply funny novel about families, the healing of old wounds, the trials and consolations of old age and - really - about the legacy of Europe''s history over the last fifty years.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/f29d8b19-8526-4efe-977c-95697b550b5c.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (114, N'We Are All Made of Glue', 77, N'Penguin Grp', N'2010-02-24 00:00:00', N'/images/books/30615cbf-0c31-4e50-9f78-225dfd540203.jpg', N'Main Hall', 1, N'Georgie Sinclair''s life is coming unstuck. Her husband''s left her. Her son''s obsessed with the End of the World. And now her elderly neighbour Mrs Shapiro has decided they are related. Or so the hospital informs her when Mrs Shapiro has an accident and names Georgie next of kin. This, however, is not a case of a quick ward visit- Mrs Shapiro has a large rickety house full of stinky cats that needs looking after that a pair of estate agents seem intent on swindling from her. Plus there are the ''Uselesses'' trying to repair it (uselessly). Then there''s the social worker who wants to put her in a nursing home. Not to mention some letters that point to a mysterious, painful past. As Georgie tries her best to put Mrs Shapiro''s life back together somehow she must stop her own from falling apart . . .', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/30615cbf-0c31-4e50-9f78-225dfd540203.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (115, N'Sister', 78, N'Little, Brown Book Grp', N'2010-09-02 00:00:00', N'/images/books/1a1b5c0c-f1db-4ada-b1ef-c3344e514594.jpg', N'Main Hall', 1, N'When Beatrice hears that her little sister, Tess, is missing, she returns home to London on the first flight available. But Bee is unprepared for the terrifying truths she must face about her younger sibling when Tess''s broken body is discovered in the snow. The police, Bee''s friends, her fiancé and even her mother accept the fact that Tess committed suicide. But nobody knows a sister like a sister, and Bee is convinced that something more sinister is responsible for Tess''s untimely death. So she embarks on a dangerous journey to discover the truth, no matter the cost.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/1a1b5c0c-f1db-4ada-b1ef-c3344e514594.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (116, N'Operation Mincemeat', 79, N'Bloomsbury Grp', N'2010-09-06 00:00:00', N'/images/books/7a3c92a7-a7ef-4224-ba80-73b63668c2bb.jpg', N'Main Hall', 1, N'From the bestselling author of Agent Zigzag. The thrilling true story of the greatest and most successful wartime deception ever attempted A Richard & Judy Book Club selection', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/7a3c92a7-a7ef-4224-ba80-73b63668c2bb.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (117, N'Take a Chance on Me', 80, N'Headline Grp', N'2010-06-24 00:00:00', N'/images/books/2a2a0597-7bb6-4fea-b01a-172f421c025c.jpg', N'Main Hall', 1, N'Jill Mansell''s novels, most recently RUMOUR HAS IT, AN OFFER YOU CAN''T REFUSE and THINKING OF YOU, regularly shoot straight into the Sunday Times bestseller list. Most recently RUMOUR HAS IT hit the No. 2 spot on the Sunday Times paperback bestseller list in July 2009.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/2a2a0597-7bb6-4fea-b01a-172f421c025c.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (118, N'Wolf Hall', 81, N'HarperCollins Grp', N'2010-03-04 00:00:00', N'/images/books/5e985efe-d01e-4d94-b596-d42ed126a14a.jpg', N'Main Hall', 1, N'In this book, "the opulant, brutal world of the Tudors comes to glittering, bloody life. It is the backdrop to the rise and rise of Thomas Cromwell: lowborn boy, charmer, bully, master of deadly intrigue, and, finally, most powerful of all Henry VIII''s courtiers."Page 4 of cover.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/5e985efe-d01e-4d94-b596-d42ed126a14a.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (119, N'The Making of Modern Britain', 82, N'Pan Macmillan Grp', N'2010-05-21 00:00:00', N'/images/books/8e3f49c6-71de-4b38-a996-636fcbc7a623.jpg', N'Main Hall', 1, N'In The Making of Modern Britain, Andrew Marr paints a fascinating portrait of life in Britain during the first half of the twentieth century as the country recovered from the grand wreckage of the British Empire. Between the death of Queen Victoria and the end of the Second World War, the nation was shaken by war and peace. The two wars were the worst we had ever known and the episodes of peace among the most turbulent and surprising. As the political forum moved from Edwardian smoking rooms to an increasingly democratic Westminster, the people of Britain experimented with extreme ideas as they struggled to answer the question â€˜How should we live?â€™ Socialism? Fascism? Feminism? Meanwhile, fads such as eugenics, vegetarianism and nudism were gripping the nation, while the popularity of the music hall soared. It was also a time that witnessed the birth of the media as we know it today and the beginnings of the welfare state. Beyond trenches, flappers and Spitfires, this is a story of strange cults and economic madness, of revolutionaries and heroic inventors, sexual experiments and raucous stage heroines. From organic food to drugs, nightclubs and celebrities to package holidays, crooked bankers to sleazy politicians, the echoes of today''s Britain ring from almost every page.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/8e3f49c6-71de-4b38-a996-636fcbc7a623.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (120, N'Life of Pi', 83, N'Canongate Grp', N'2003-05-17 00:00:00', N'/images/books/f4d8ffdf-55c1-4b34-9879-12ccd0c993c7.jpg', N'Main Hall', 1, N'After the sinking of a crago ship, a solitary lifeboat remains bobbing on the wild blue Pacific. The only survivors from the wreck are a sixteen-year-old boy named Pi, a hyena, a wounded zebra, and orangutan-and a 450-pound Royal Bengel tiger.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/f4d8ffdf-55c1-4b34-9879-12ccd0c993c7.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (121, N'No.1 Ladies'' Detective Agency,The:No.1 Ladies'' Detective Agency S.', 84, N'Little, Brown Book Grp', N'2003-06-05 00:00:00', N'/images/books/631b233a-3cd4-4fd7-b387-5a76b07afa0f.jpg', N'Main Hall', 1, N'', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/631b233a-3cd4-4fd7-b387-5a76b07afa0f.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (122, N'Tea Time for the Traditionally Built', 84, N'Little, Brown Book Grp', N'2010-02-04 00:00:00', N'/images/books/08630285-e88f-4836-997d-be8dad433178.jpg', N'Main Hall', 1, N'It is a troublesome fact on which even Mma Ramotswe and her assistant Mma Makutsi agree: there are things that men know and ladies do not, and vice versa. It is unfortunate, for example, when Mma Ramotswe''s newest client is the big-shot owner of the ailing Kalahari Swoopers, that one thing lady detectives know very little about is football. And when the glamorous Violet Sephotho sets her sights on Mma Makutsi''s unsuspecting fiancé, it becomes exasperatingly clear that some men do not know how to recognise a ruthless Jezebel even when she is bouncing up and down on the best bed in the Double Comfort Furniture Shop. In her attempt to foster understanding between the sexes and find the traitor on Mr Football''s team, Mma Ramotswe ventures into new territory, drinks tea in unfamiliar kitchens and learns to trust in the observational powers of small boys. And, as wise and warm-hearted as his heroine, Alexander McCall Smith reminds us that we must dig deep to uncover the goodness of the human heart.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/08630285-e88f-4836-997d-be8dad433178.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (123, N'The Road', 85, N'Pan Macmillan Grp', N'2009-05-01 00:00:00', N'/images/books/aa886ccd-022b-4d84-8b3a-ee21a84fd8cc.jpg', N'Main Hall', 1, N'Tells the story of a father and son walking alone through burned America, heading through the ravaged landscape to the coast.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/aa886ccd-022b-4d84-8b3a-ee21a84fd8cc.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (124, N'Angela''s Ashes', 86, N'HarperCollins Grp', N'1997-05-06 00:00:00', N'/images/books/ae2c7ba7-2fa1-46a2-b763-b8a4d9c71458.jpg', N'Main Hall', 1, N'Frank McCourt returned to America when he was nineteen. For many years, he was an English teacher at Stuyvesant High School in New York City. The sequel to "Angela''s Ashes, ''Tis, " will be published in the fall of 1999. McCourt lives in Connecticut.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/ae2c7ba7-2fa1-46a2-b763-b8a4d9c71458.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (125, N'Fever of the Bone', 87, N'Little, Brown Book Grp', N'2010-02-18 00:00:00', N'/images/books/a769d28f-90c7-4ee4-972f-a529ae4f1081.jpg', N'Main Hall', 1, N'You should have been a detective. If there''s one thing the last year has proved, it''s how good you are at finding things out. Not simple things. Hard things. Things that nobody is supposed to be able to find out. Things that are buried so deep nobody even thinks twice about them. The sort of things that turn people''s lives inside out once they''re exposed.'' Meet Tony Hill''s most twisted adversary - a killer with a shopping list of victims, a killer unmoved by youth and innocence, a killer driven by the most perverted of desires. The murder and mutilation of teenager Jennifer Maidment is horrific enough on its own. But it''s not long before Tony realises it''s just the start of a brutal and ruthless campaign that''s targeting an apparently unconnected group of young people. Struggling with the newly-awakened ghosts of his own past and desperate for distraction in his work, Tony battles to find the answers that will give him personal and professional satisfaction in his most testing investigation yet.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/a769d28f-90c7-4ee4-972f-a529ae4f1081.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (126, N'Atonement', 88, N'Random House Grp', N'2002-05-02 00:00:00', N'/images/books/65e8ddda-a405-4fbb-8610-ad03a4b20844.jpg', N'Main Hall', 1, N'Fledgling writer Briony Tallis, as a 13-year-old, irrevocably changes the course of several lives when she accuses her older sister''s lover of a crime he did not commit.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/65e8ddda-a405-4fbb-8610-ad03a4b20844.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (127, N'Solar', 88, N'Random House Grp', N'2010-03-18 00:00:00', N'/images/books/3bd2fbe7-d7fb-4833-94d5-4191f76cd145.jpg', N'Main Hall', 1, N'Michael Beard ist a Nobel prize-winning physicist whose best work is behind him. A compulsive womaniser, Beard finds his fifth marriage floundering. But this time it is different: his wife is having the affair, an he is still in love with her.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/3bd2fbe7-d7fb-4833-94d5-4191f76cd145.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (128, N'Life and Laughing', 89, N'Penguin Grp', N'2010-10-14 00:00:00', N'/images/books/0a9ae089-ca51-43d0-be2b-29dd8f3fbf7c.jpg', N'Main Hall', 1, N'MICHAEL McINTYRE has become Britain''s biggest comedy star. His debut stand-up DVD, Live & Laughing, was the fastest selling of all time, only to be eclipsed by his second, Hello Wembley, that sold over 1.4 million copies and was the 2009 Christmas number one. He hosts his own BAFTA nominated BBC1 series, Michael McIntyre''s Comedy Roadshow, and won the British Comedy Award for Best Live Stand-up in 2009 following his record breaking fifty-four date Arena tour. But how did he get there? Michael reveals all in his remarkably honest and hilarious autobiography Life & Laughing. His showbiz roots, his appalling attempts to attract the opposite sex, his fish-out-of-water move from public to state school and his astonishing journey from selling just one ticket at the Edinburgh Festival to selling half a million tickets on his last tour. Michael''s story is riveting, poignant, romantic and above all very, very funny.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/0a9ae089-ca51-43d0-be2b-29dd8f3fbf7c.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (129, N'You are what You Eat', 90, N'Penguin Grp', N'2004-06-17 00:00:00', N'/images/books/6292c7b9-b287-472c-a8d0-a54aea14937b.jpg', N'Main Hall', 1, N'<p>Dr Gillian McKeith has turned round Britain''s Worst Eaters with incredible results. Now discover her fabulous diet secrets and get ready to meet the new you. Take the food IQ test and find out what your diet is doing to you. Banish your cravings. Prevent food-related health problems such as diabetes. Discover how small changes are going to make a big difference. Eat more . . . not less. Follow Dr McKeith''s simple advice and seven-day jumpstart plan and you''ll look and feel fantastic. You''ll be amazed at the difference a few changes can make, and how easy it can be. Just give it a go and see . . .</p>', 0, N'2022-10-14 05:56:47', N'2022-10-14 22:34:55', N'/images/books/thumb/6292c7b9-b287-472c-a8d0-a54aea14937b.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (130, N'I Can Make You Thin', 91, N'Transworld Grp', N'2005-01-17 00:00:00', N'/images/books/3611388e-0f03-4fd0-a643-45f6eac44f1a.jpg', N'Main Hall', 1, N'The easy way to lose weight and stay slim — with a free weight-loss CD included. Do you want to lose weight easily? Would you like to feel really happy with your body? Are you unable to lose those last 10 pounds? Are you a late night snacker? Do you find it hard to say no to second helpings? Do you get disheartened about your eating habits and your weight? Yes? Well, Paul McKenna has written the book for you.I Can Make You Thinwill change your attitude to food forever.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/3611388e-0f03-4fd0-a643-45f6eac44f1a.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (133, N'Stupid White Men', 94, N'Penguin Grp', N'2002-10-03 00:00:00', N'/images/books/c2ab7935-9c7a-4caf-b65d-9dde3b5f2dd9.jpg', N'Main Hall', 1, N'This text tells you everything you need to know about how the great and the good screw us over. It reveals - among other things - how President Bush stole an election aided only by his brother, cousin, his dad''s cronies, electoral fraud and tame judges; how the rich stay rich while forcing the rest of us to live in economic fear; and how politicians have whored themselves to big business. Not to mention providing detail on just how stupid Bush is, a convincing case for male obsolescence and a fabulous new use for Whites Only signs', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/c2ab7935-9c7a-4caf-b65d-9dde3b5f2dd9.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (134, N'The Time Traveller''s Guide to Medieval England', 95, N'Random House Grp', N'2009-10-01 00:00:00', N'/images/books/e7fee647-77d3-4f3d-a835-e8f801b057f9.jpg', N'Main Hall', 1, N'The past is a foreign country. This is your guide book. Imagine you could get into a time machine and travel back to the fourteenth century.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/e7fee647-77d3-4f3d-a835-e8f801b057f9.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (135, N'The House at Riverton', 96, N'Pan Macmillan Grp', N'2007-06-15 00:00:00', N'/images/books/d6e7789a-0682-47c8-907f-6fdf673d761f.jpg', N'Main Hall', 1, N'This gorgeous debut novel is set in England between the two World Wars. The story of an aristocratic family, a mysterious death, and a vanishing way of life is told in flashback by a woman who witnessed it all and kept secrets for more than 50 years.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/d6e7789a-0682-47c8-907f-6fdf673d761f.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (136, N'Labyrinth', 97, N'Orion Grp', N'2006-01-11 00:00:00', N'/images/books/5d999c0b-130e-4ff4-bab7-42d08ba586a2.jpg', N'Main Hall', 1, N'July 1209: in Carcassonne a seventeen-year-old girl is given a mysterious book by her father which he claims contains the secret of the true Grail. Although Alais cannot understand the strange words and symbols hidden within, she knows that her destiny lies in keeping the secret of the labyrinth safe... July 2005: Alice Tanner discovers two skeletons in a forgotten cave in the French Pyrenees. Puzzled by the labyrinth symbol carved into the rock, she realises she''s disturbed something that was meant to remain hidden. Somehow, a link to a horrific past - her past - has been revealed.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/5d999c0b-130e-4ff4-bab7-42d08ba586a2.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (137, N'The Winter Ghosts', 97, N'Orion Grp', N'2010-10-28 00:00:00', N'/images/books/834adb03-8e72-4e1a-b106-98da8d073dd9.jpg', N'Main Hall', 1, N'The Great War took much more than lives. It robbed a generation of friends, lovers and futures. In Freddie Watson''s case, it took his beloved brother and, at times, his peace of mind. Unable to cope with his grief, Freddie has spent much of the time since in a sanatorium. In the winter of 1928, still seeking resolution, Freddie is travelling through the French Pyrenees - another region that has seen too much bloodshed over the years. During a snowstorm, his car spins off the mountain road. Shaken, he stumbles into the woods, emerging by a tiny village. There he meets Fabrissa, a beautiful local woman, also mourning a lost generation. Over the course of one night, Fabrissa and Freddie share their stories of remembrance and loss. By the time dawn breaks, he will have stumbled across a tragic mystery that goes back through the centuries. By turns thrilling, poignant and haunting, this is a story of two lives touched by war and transformed by courage.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/834adb03-8e72-4e1a-b106-98da8d073dd9.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (138, N'The Snowman', 98, N'Random House Grp', N'2010-08-19 00:00:00', N'/images/books/fd986107-abe6-4e34-bddf-2ea645146e5f.jpg', N'Main Hall', 1, N'The night the first snow falls a young boy wakes to find his mother gone. He walks through the silent house, but finds only wet footprints on the stairs. In the garden looms a solitary figure: a snowman bathed in cold moonlight, its black eyes glaring up at the bedroom windows. Round its neck is his mother''s pink scarf.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/fd986107-abe6-4e34-bddf-2ea645146e5f.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (140, N'The Time Traveler''s Wife', 100, N'Random House Grp', N'2004-05-29 00:00:00', N'/images/books/50fb9102-4204-42c3-bcba-332a572173e0.jpg', N'Main Hall', 1, N'A New York Times Bestseller. "An enchanting novel, beautifully crafted and as dazzlingly imaginative as it is dizzyingly romantic.Scott Turow', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/50fb9102-4204-42c3-bcba-332a572173e0.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (141, N'Her Fearful Symmetry', 100, N'Random House Grp', N'2010-07-05 00:00:00', N'/images/books/0d540260-5231-4933-9bb0-ae67730139e0.jpg', N'Main Hall', 1, N'Julia and Valentina Poole are identical twins who have no interest in college, jobs or anything outside their cosy suburban home. But everything changes when they receive notice that an aunt whom they didn''t know existed has died and left them her flat in an apartment block overlooking Highgate Cemetery in London.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/0d540260-5231-4933-9bb0-ae67730139e0.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (142, N'Tickling the English', 101, N'Penguin Grp', N'2010-04-01 00:00:00', N'/images/books/c2a49ae6-e1f6-4288-8d4a-5f417f361533.jpg', N'Main Hall', 1, N'Originally published: 2009 by Michael Joseph.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/c2a49ae6-e1f6-4288-8d4a-5f417f361533.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (143, N'Dreams from My Father', 102, N'Canongate Grp', N'2008-06-05 00:00:00', N'/images/books/132a28bb-49d2-4507-82ad-3e1343a44b38.jpg', N'Main Hall', 1, N'The son of a black African father and a white American mother, Obama was only two years old when his father walked out on the family. Many years later, Obama receives a phone call from Nairobi: his father is dead. This sudden news inspires an emotional odyssey for Obama, determined to learn the truth of his father''s life and reconcile his divided inheritance.Written at the age of thirty-three, Dreams from my Father is an unforgettable read. it illuminates not only Obama''s journey, but also our universal desire to understand our history, and what makes us the people we are.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/132a28bb-49d2-4507-82ad-3e1343a44b38.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (144, N'At My Mother''s Knee... and Other Low Joints', 103, N'Transworld Grp', N'2008-09-24 00:00:00', N'/images/books/f990c81b-b227-4949-b772-de88b4b4180f.jpg', N'Main Hall', 1, N'The story of Paul O''Grady''s early life in Irish Catholic Birkenhead that started him on the long and winding road from mischievous altar boy to national treasure.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/f990c81b-b227-4949-b772-de88b4b4180f.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (145, N'The Devil Rides Out', 103, N'Transworld Grp', N'2010-09-16 00:00:00', N'/images/books/5617295f-4b45-4b97-bf1c-d2be6512d7f3.jpg', N'Main Hall', 1, N'Following on from the enormous success of At My Mother''s Knee, Paul O''Grady here continues his extraordinary life story ... Aged 18, the young Paul has already been a cat burglar and a boxer, a mischievous altar boy and a civil servant, he''s fathered a baby and lost his own dad, he''s discovered Liverpool''s burgeoning gay club scene as well as what goes on down at the Docks. Having crammed so much into his early life, surely there couldn''t be much still left to do? Rest assured, there''s never a dull moment in this riotous account of What Paul Did Next.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/5617295f-4b45-4b97-bf1c-d2be6512d7f3.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (146, N'Jamie''s 30-Minute Meals', 104, N'Penguin Grp', N'2010-09-30 00:00:00', N'/images/books/b1ca9d8a-a145-47ed-ac6d-f69938f99db3.jpg', N'Main Hall', 1, N'This year, I''ve got the message loud and clear that as everyone comes under bigger and bigger financial pressure, they want help to cook tasty, nutritious food on a budget - so this book was born completely out of public demand. It draws on knowledge and cooking skills to help you make better choices, showing you how to buy economically and efficiently, get the most out of your ingredients, save time and prevent food waste. And there''s no compromise - I''m talking big flavours, comfort food that makes you happy, and colourful, optimistic dishes. As well as that, every single recipe in the book is cheaper per portion than your average takeaway, so every choice is a great value choice. Our biggest luxury is knowledge, whether times are hard or not, so get kitchen smart and smash the recession.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/b1ca9d8a-a145-47ed-ac6d-f69938f99db3.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (147, N'Jamie''s Ministry of Food', 104, N'Penguin Grp', N'2008-10-03 00:00:00', N'/images/books/4c8d666b-4260-4987-a836-52a9f04c59b4.jpg', N'Main Hall', 1, N'Jamie Oliver will teach you to cook from scratch in the Ministry of Food.Sixty years ago food was in short supply and malnutrition rates were high', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/4c8d666b-4260-4987-a836-52a9f04c59b4.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (148, N'Jamie at Home', 104, N'Penguin Grp', N'2007-09-06 00:00:00', N'/images/books/259c1b9b-285b-4b7b-b170-c6084eb48d51.jpg', N'Main Hall', 1, N'This book is very close to my heart. It''s about no-nonsense, simple cooking with great flavours all year round. When I began writing it, I didn''t really know what recipes I would come up with, but something began to inspire me very quickly. . . my vegetable patch! I came to realize last year that it''s not always about looking out at the wider world for inspiration. Being at home, feeling relaxed and open, can also offer this. I love to spend time at home in the village where I grew up, working with the boss, Mother Nature, in my garden and seeing all my beautiful veggies coming out of the ground. Inside you''ll find over 100 new recipes, plus some basic planting information and tips if you fancy having a go at getting your hands dirty as well!', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/259c1b9b-285b-4b7b-b170-c6084eb48d51.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (149, N'The Return of the Naked Chef', 104, N'Penguin Grp', N'2000-03-30 00:00:00', N'/images/books/5a5e5973-eb66-4fd2-8ed5-b501fe2a7154.jpg', N'Main Hall', 1, N'Number one for over 5 months in Britain with The Naked Chef , the bestselling, brilliant Jamie Oliver is back with a brand spanking new book to accompany a second cracking BBC TV series. There is no doubt that Jamie has captured the hearts of the nation with his passion for simple, delicious, home-cooked food. His talent his huge, his style relaxed and his recipes divine. If you think cooking is boring or time-consuming or you haven''t the confidence to chuck out those ready-made meals, let Jamie change all that for you. Here he returns to get you into the kitchen with his mouth-watering recipes and new angles on favourite dishes - from the most fantastic breakfasts, tapas and bevies to robust roasts, funky fish dishes and desserts to die for. Filled with fresh ingredients, pukka recipes and boundless enthusiasm, The Return of the Naked Chef is definitely something not to be missed.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/5a5e5973-eb66-4fd2-8ed5-b501fe2a7154.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (150, N'Happy Days with the Naked Chef', 104, N'Penguin Grp', N'2001-09-03 00:00:00', N'/images/books/fff502de-eaf0-4e0e-8703-4286a8e5c1dc.jpg', N'Main Hall', 1, N'For Jamie Oliver, food is all about happy days - not just cooking superbly fresh and funky ingredients, but good fun, great eating and top-quality time spent with friends and family. Now Jamie, one of Britain''s best-loved chefs, gives us Happy Days, his third book, to accompany his fantastic new series of The Naked Chef on BBC2. Jamie believes in finding the best ingredients and making tasty, easy, sociable food with the minimum of fuss. Like his first two books, Happy Days is filled with fantastic salads, pastas, meat, fish, breads and desserts for all occasions. Enjoy yourself, get stuck in . . . Happy days!', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/fff502de-eaf0-4e0e-8703-4286a8e5c1dc.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (151, N'Jamie Does', 104, N'Penguin Grp', N'2010-04-15 00:00:00', N'/images/books/0b570d31-d496-4aad-8501-a3c403f7c421.jpg', N'Main Hall', 1, N'This cookbook will celebrate the vibrant food of six very different countries. Each chapter will focus on a different city or region: Marrakech, Athens, Venice, Andalucia, Stockholm and the Ardeche region of France. The food in each chapter will be a mixture of classic dishes and new recipes inspired by the things Jamie learns on his travels. Beautiful reportage photography of the faces, places and ingredients will accompany each chapter. Jamie says: ''The food I''ve embraced on each trip is a mixture of what you could call the clichéd star dishes - the tagines of Morocco, the flamboyant paella of Spain and the zingy fresh flavours of a classic Greek salad - and the recipes that I''ve been inspired to make after walking through the markets and soaking up the vibes of each place. What you''ll find in this book is fun, optimistic, escapist food you can actually cook and enjoy in your own home.''', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/0b570d31-d496-4aad-8501-a3c403f7c421.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (152, N'Jamie''s Italy', 104, N'Penguin Grp', N'2005-10-03 00:00:00', N'/images/books/a13c42e8-01a0-4d57-a413-a54289a48208.jpg', N'Main Hall', 1, N'AUTHENTIC ITALY DELICIOUS ITALY JAMIE''S ITALY Italy has inspired Jamie Oliver throughout his career. His ambition has always been to travel across the country on a quest to capture the very essence of Italian cooking - and to produce the best and simplest Italian cookbook for everybody to enjoy. Jamie''s Italy is a result of that journey - and it''s a land of plenty. With more than 120 brand-new recipes for everything from risotto to roasts and spaghetti to stews, and structured like a traditional trattoria menu, Jamie''s Italy takes you all over Italy to cook with and learn from the real masters of Italian cuisine: the locals. Far from the standard ''lemons and olives'' version of Italian cooking, it is a cookbook by the people for the people. From Sicily to Tuscany, it''s about the local fishermen, family bakers and, of course, the ''Mamas'', sharing their recipes and the tips that have gone into their cooking for generations. But it''s not only mouth-watering food that Jamie brings back home: it''s also the spirit that makes cooking and eating absolutely central to family life whichever part of Italy you''re in. Bursting with the warmth and hospitality of real family life, this is both a superbly accessible cookbook and a unique travelogue and diary, in which you''ll find the authentic flavour of Italy and the people who live there. If you love quality food prepared with genuine passion - you''ll never want to leave Jamie''s Italy.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/a13c42e8-01a0-4d57-a413-a54289a48208.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (153, N'A Simples Life', 105, N'Random House Grp', N'2010-10-28 00:00:00', N'/images/books/53a772df-529a-49b4-9aa1-0497c4fcfc45.jpg', N'Main Hall', 1, N'Aleksandr Orlov has in the last year become one of the most loved figures in British culture and his catchphrase - Simples! - can be heard from the playground to the office. This autobiography offers the same humour as his TV ads, giving us the full story of his ancestor''s Journey of Courageousness from the Kalahari to Russia.', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/53a772df-529a-49b4-9aa1-0497c4fcfc45.jpg', NULL)
INSERT INTO [dbo].[Books] ([Id], [Title], [AuthorId], [Publisher], [PublishingDate], [ImageUrl], [Hall], [IsAvailableForRental], [Description], [IsDeleted], [CreatedOn], [LastUpdatedOn], [ImageThumbnailUrl], [ImagePublicId]) VALUES (155, N'Man and Boy', 107, N'HarperCollins Grp', N'2000-03-06 00:00:00', N'/images/books/ff3e701b-667a-4e7c-98ae-5e187fc0b54a.jpg', N'Main Hall', 1, N'A Fabulously Engaging Novel, A Sunday Times Number 1 Bestseller, With Over 1.2 Million Copies Sold. Harry Silver Has It All. A Successful Job In Tv, A Gorgeous Wife, A Lovely Child. And In One Moment Of Madness, He Chucks It All Away. Man And Boy Is The Story Of How He Comes To Terms With His Life And Achieves A Degree Of Self-Respect, Bringing Up His Son Alone And, Gradually, Learning What Words Like Love And Family Really Mean. Very Well-Written, Pacy, Funny And Heart-Breakingly Moving. ''Parsons Has Written A Sharp, Witty And Wise Book Straight From His Heart. His Characters Are All Nitty-Gritty, Bounce-Off-The-Page, Real People; His Dialogue Is Brilliant.'' Daily Mail', 0, N'2022-10-14 05:56:47', NULL, N'/images/books/thumb/ff3e701b-667a-4e7c-98ae-5e187fc0b54a.jpg', NULL)
